import React, { useState } from 'react'
import'./CSS/Loginsignup.css'

const LoginSignup = () => {
  //const [state,setState] = useState({Signup})
  const [formData,setFormData] = useState({
    username:"",
    email:"",
    password:""
  })

  const changeHandler = (e)=>{
    setFormData({...formData,[e.target.name]:e.target.value})
  }

  const signup = async (e)=>{
    e.preventDefault()
    console.log("signup page", formData)
    let responseData;
    await fetch('http://localhost:4000/signup',{
      method:'POST',
      headers:{
        Accept:'application/form-data',
        'Content-Type':'application/json',
      },
      body:JSON.stringify(formData),
    }).then(res=>res.json()).then(data=>responseData=data)
    if(responseData.success){
      localStorage.setItem('auth-token',responseData.token);
      window.location.replace("/");
    }
    else{
      alert(responseData.errors)
    }
  }
  return (
    <div className='loginsignup'>
    <div className='loginsignup-container'>
      <h1>Signup</h1>
      <div className="loginsignup-fields">
        <input type="text" placeholder='your name' name='username' value={formData.username} onChange={changeHandler}/>
        <input type="email" placeholder='your emailid' name='email' value={formData.email} onChange={changeHandler}/>
        <input type="password" placeholder='password' name='password' value={formData.password} onChange={changeHandler} />
      </div>
      <button onClick={signup}>Submit</button>

        <p className="loginsignup-login">Already have an account <span>click here</span></p>
        {/* <div className="loginsignup-agree">
          <input type="checkbox" name='' id='' />
          <p>By continuing, I agree to the terms of use and privacy policy</p>
        </div> */}
      </div>
      </div>
  )
}

export default LoginSignup
