import React, { useState } from 'react'
import './Newsletter.css'

const Newsletter = () => {
const[inputValue, setInputValue] = useState('');

const handleChange = (event) => {
  setInputValue(event.target.value);
};
  function emailValidation(){
    var validRegex = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;
    if (inputValue.value){
    if(inputValue.value.match(validRegex)) {
      alert("Subscription Saved!");
      document.form1.text1.focus();
      return true;
     } else {
       alert("Invalid email address, Please try again!");
      document.form1.text1.focus();
      return false;
    }
  }
  }
  return (
    <div className='newsletter'>
        <h1>Get Exclusive offers on your email.

        </h1>
        <p>Be the first to know about the latest deals and more!</p>
        <div>
            <input type="email" value={handleChange} placeholder='Enter your Email Id' />
            <button onClick={emailValidation}>Subscribe</button>
        </div>
    </div>
  )
}


export default Newsletter
